mapper.select:
SELECT ADMINISTRATOR_NUMBER
FROM TBL_ADMINISTRATOR
WHERE ADMINISTRATOR_ID = #{administratorId} AND ADMINISTRATOR_PASSWORD = #{administratorPassword}

SELECT PRODUCT_NUMBER, PRODUCT_NAME, PRODUCT_CONTENT, PRODUCT_PRICE, PRODUCT_SALE
FROM (
    SELECT ROWNUM RNUM, PRODUCT_NUMBER, PRODUCT_NAME, PRODUCT_CONTENT, PRODUCT_PRICE, PRODUCT_SALE
    FROM (
        SELECT PRODUCT_NUMBER, PRODUCT_NAME, PRODUCT_CONTENT, PRODUCT_PRICE, PRODUCT_SALE
        FROM TBL_PRODUCT
        ORDER BY PRODUCT_NUMBER
    )

    WHERE ROWNUM <=  #{page} * #{amount}

)
WHERE RNUM > (#{page} - 1) * #{amount}

SELECT RNUM, REVIEW_NUMBER, USER_NUMBER, REVIEW_CONTENT FROM(SELECT ROWNUM RNUM ,REVIEW_NUMBER, USER_NUMBER, REVIEW_CONTENT FROM(SELECT REVIEW_NUMBER, USER_NUMBER, REVIEW_CONTENT FROM TBL_SHOP_REVIEW ORDER BY REVIEW_NUMBER)
WHERE ROWNUM <= #{page} * #{amount})
WHERE RNUM > (#{page} - 1) * #{amount}

SELECT RNUM, USER_NUMBER, USER_ID, USER_NAME, USER_EMAIL, TO_CHAR(USER_BIRTH,'YYYY/MM/DD') USER_BIRTH, USER_PHONE_NUMBER
FROM(SELECT  ROWNUM RNUM, USER_NUMBER, USER_ID, USER_NAME, USER_EMAIL, USER_BIRTH, USER_PHONE_NUMBER
FROM (SELECT USER_NUMBER, USER_ID, USER_NAME, USER_EMAIL, USER_BIRTH, USER_PHONE_NUMBER
FROM TBL_USER
ORDER BY USER_NUMBER
)

WHERE ROWNUM <= #{page} * #{amount}
)

WHERE RNUM > (#{page} - 1) * #{amount}

SELECT RNUM, ORDER_NUMBER, USER_NUMBER, PRODUCT_NUMBER, ORDER_PRODUCT_CNT, ORDER_TOTAL, TO_CHAR(ORDER_DATE,'YYYY/MM/DD') ORDER_DATE, ORDER_ADDR, ORDER_ADDR_DETAIL FROM(SELECT ROWNUM RNUM, ORDER_NUMBER, USER_NUMBER, PRODUCT_NUMBER, ORDER_PRODUCT_CNT, ORDER_TOTAL, ORDER_DATE, ORDER_ADDR, ORDER_ADDR_DETAIL FROM(SELECT ORDER_NUMBER, USER_NUMBER, PRODUCT_NUMBER, ORDER_PRODUCT_CNT, ORDER_TOTAL, ORDER_DATE, ORDER_ADDR, ORDER_ADDR_DETAIL FROM(SELECT O.ORDER_NUMBER, O.USER_NUMBER, P.PRODUCT_NUMBER, P.ORDER_PRODUCT_CNT, O.ORDER_TOTAL, O.ORDER_DATE, O.ORDER_ADDR, O.ORDER_ADDR_DETAIL FROM TBL_ORDER O JOIN TBL_ORDER_PRODUCT P ON O.ORDER_NUMBER = P.ORDER_NUMBER) ORDER BY ORDER_NUMBER)
WHERE ROWNUM <= #{page} * #{amount})
WHERE RNUM > (#{page} - 1) * #{amount}

SELECT RNUM, FAQ_NUMBER, FAQ_QUESTION, FAQ_ANSWER FROM(SELECT ROWNUM RNUM, FAQ_NUMBER, FAQ_QUESTION, FAQ_ANSWER FROM(SELECT FAQ_NUMBER, FAQ_QUESTION, FAQ_ANSWER FROM TBL_FAQ ORDER BY FAQ_NUMBER)
WHERE ROWNUM <= #{page} * #{amount})
WHERE RNUM > (#{page} - 1) * #{amount}

SELECT RNUM, USER_NUMBER, USER_NAME, USER_POINT FROM(SELECT ROWNUM RNUM, USER_NUMBER, USER_NAME, USER_POINT FROM(SELECT USER_NUMBER,USER_NAME, USER_POINT FROM TBL_USER ORDER BY USER_NUMBER)
WHERE ROWNUM <= #{page} * #{amount})
WHERE RNUM > (#{page} -1) * #{amount}
